import React,{ useState , useEffect} from 'react';
import { useHistory,useParams } from 'react-router-dom';
import './AddEdit.css';
import fireDb from "../firebase";
import "firebase/database";
import { toast } from 'react-toastify';

const initialState = {
    uname : "",
    email : "",
    contact : "",
    age :"",
    skills :"",
    roll :"",
    interests :"",
}

function AddEdit() {
    const [values , setValues] = useState(initialState);

    const {uname,email,contact,age,skills,roll,interests} = values;

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setValues(
            {
                ...values,
                [name] : value,
            }
        )
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        if(!uname || !email || !contact || !age || !skills || !roll || !interests)
        {
            toast.error("Please Provide all inputs")
        } else 
        {
        //     fireDb.child("members").push()
         }
    };

    return (
            <div style={{marginTop: "100px"}}>
            <form style={{ 
                margin: "auto", 
                padding: "15px", 
                maxWidth: "400px", 
                alignContent: "center" 
            }}
              onSubmit = {handleSubmit}
            >
            <label htmlFor="uname">Name</label>
            <input 
                type="text" 
                id="uname" 
                placeholder="Enter your name.." 
                value={uname} 
                onChange={handleInputChange}
            />  

            <label htmlFor="email">Email</label>
            <input 
                type="text" 
                id="email" 
                placeholder="Enter your Email.." 
                value={email} 
                onChange={handleInputChange}
            />  

             <label htmlFor="contact">Contact</label>
            <input 
                type="tel"  
                id="contact" 
                placeholder="Enter your Phone no.." 
                value={contact} 
                onChange={handleInputChange}
            />  

           <label htmlFor="age">Age</label>
            <input 
                type="number" 
                id="age" 
                placeholder="Enter your Age.." 
                value={age} 
                onChange={handleInputChange}
            />  

            <label htmlFor="skills">Skills</label>
            <input 
                type="text" 
                id="skills" 
                placeholder="Enter your skills.." 
                value={skills} 
                onChange={handleInputChange}
            />  

            <label htmlFor="interests">Interests</label>
            <input 
                type="text" 
                id="interests" 
                placeholder="Enter your name.." 
                value={interests} 
                onChange={handleInputChange}
            />    

            <label htmlFor="roll">Roll in Club</label>
            <input 
                type="text" 
                id="roll" 
                placeholder="Enter your Roll.." 
                value={roll} 
                onChange={handleInputChange}
            />    

            <input type="submit" value="SAVE"/>

            </form>
        </div>
    );
}

export default AddEdit;
